    super ()
    this.state ={
        city: "",
        weather:"",
        temp: ""
    }
  }


  componentDidMount () {  
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&units=metric&APPID=${ApiKey}`)
    .then(response => response.json())
    .then(data => {

      this.setState({
       city: data.name,
       weather: data.weather[0].main,
       temp: data.main.temp,
      })
    })
  }


