import React from 'react'

const ApiKey = "040d742b4d477bb2a9bac9151e48fa50"

class Weather extends React.Component {
    constructor(){
        super ()
        this.state ={
            weather:{}
        }
    }
    componentDidMount () {  
        fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${ApiKey}`)
        .then(response => response.json())
        .then(data => {
            let weatherDetails = data.response.map((detail) => {
                return(
                    <div key = {detail.response}>
                        <p>{detail.main.temp} </p>
                        <p>{detail.weather.main}</p>
                        <img src = {detail.weather.icon}/>
                    </div>
                )
            })
          this.setState({
           weather: weatherDetails
          })
        })
      }

    render(){

        return (
        <div>

        </div>
        )}
}

export default Weather;
