        return (
        <div className = "weather">
          <h3> {this.state.error} </h3>

          <h1 id="cityName"> {this.state.city} </h1> 
          <strong><p id="description"> {this.state.weather} </p></strong>
          <p id="temp">{ this.state.temp}  </p>


        </div>

        )}
}

export default Weather;
