import React from 'react'

class Weather extends React.Component {
    constructor(){
        super ()
        this.state ={
            loading: false,
            weather:{}
        }
    }
    componentDidMount () {
        this.setState({loading: true})
        fetch("api.openweathermap.org/data/2.5/weather?id=2643743&APPID=040d742b4d477bb2a9bac9151e48fa50")
        .then(id => id.json())
        .then(data => {
          this.setState({
            loading: false,
            weather:data
          })
        })
      }
    render(){

        const display= this.state.loading? "loading...":this.state.weather.weather
        return (
        <div>
           <h3>{display}</h3> 
        </div>
        )}
}

export default Weather;
